package com.telusko.learn.Basics;

public class DowhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//irrespective of while condition
		//do executes once
		
		int start = 11;
		int n = 10;
		
		do
		{
			System.out.print(start+" ");
			start++;
		}while(start<=n);
		
	}

}
