package com.telusko.learn.Basics;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 1;
		
		for(;a<=5;)
		{
			System.out.println(a);
			a++;
		}
		
		for(int i=1;i<=5;i++)
		{
			System.out.print(i+" ");
		}
		
		
	}

}
